declare type Descriptor = any

declare type Comparison =
  | {
      pass: true
    }
  | {
      pass: false
    }
  | {
      actual: Descriptor
      expected: Descriptor
      pass: boolean
    }

declare type Diff = string

declare namespace concordance {
  function compare<T>(actual: T, expected: T): Comparison
  function diff<T>(actual: T, expected: T): Diff
}

export = concordance
